def scrape_noon_search(query, page):
    """Scrape product listings from Noon search results."""
    # url = f"https://www.noon.com/uae-en/search/?q={query}&page={page}"
    url = f"https://www.noon.com/_vs/nc/mp-customer-catalog-api/api/v3/u/search/?q={query}&page={page}"
    options = {'ajax_wait': 'true', 'page_wait': '5000'}

    response = crawling_api.get(url, options)

    if response['headers']['pc_status'] == '200':
        return response['body'].decode('utf-8')
    else:
        print(f"Failed to fetch page {page}.")
        return None


def extract_product_data(onresult):
    """Extract product details from Noon search results."""
    # soup = BeautifulSoup(html, 'html.parser')
    products = []

    # 解析为Python字典
    data = json.loads(onresult)

    # for item in soup.select('div.grid > span.productContainer'):
    for item in data["hits"]:
        # title = item.select_one('div[data-qa="product-name"]').text.strip() if item.select_one(
        #     'div[data-qa="product-name"]') else ''
        title = item["name"]
        # price = item.select_one('strong.amount').text.strip() if item.select_one('strong.amount') else ''
        price = item["price"]
        product_rating_count = get_nested_value(item, ['product_rating', 'count'], 0)
        variant_overlay_url = get_nested_value(item, ['variant_overlay_url'], '')
        url = item["url"]
        sku = item["sku"]
        offer_code = item["offer_code"]
        product_url = f"https://www.noon.com/uae-en/{url}/{sku}/p/?o={offer_code}"
        if variant_overlay_url:
            product_url = f"https://www.noon.com/uae-en/{variant_overlay_url}"
        # currency = item.select_one('span.currency').text.strip() if item.select_one('span.currency') else ''
